$(document).ready(function(){
    $("html,body").animate({scrollTop: 0}, 1000);
    var x = document.getElementById("audio").autoplay;
    var x = document.getElementById("audio").volume = 0.2;
    var playing = true;
    var backAudio = $('#audio');
        $('#sound').click(function() {
            $(this).toggleClass("down");
             if (playing == false) {
                playing = true;
                document.getElementById('audio').play();
                document.getElementById("bar-1").classList.remove("noAnim");
                document.getElementById("bar-2").classList.remove("noAnim");
                document.getElementById("bar-3").classList.remove("noAnim");
                document.getElementById("bar-4").classList.remove("noAnim");
                backAudio.animate({volume: 0.2}, 900);
            } else {
                document.getElementById("bar-1").classList.add("noAnim");
                document.getElementById("bar-2").classList.add("noAnim");
                document.getElementById("bar-3").classList.add("noAnim");
                document.getElementById("bar-4").classList.add("noAnim");
                backAudio.animate({volume: 0}, 900, function () {
                    document.getElementById('audio').pause();
                    playing = false;
                });
            }
        });
        var songs = [
          "../audio/background.mp3",
          "../audio/OVERDRIVE.mp3",
          "../audio/goodvibes.mp3"
        ];
      function getPlayer() {
          var a = document.getElementById("audio");
          a.addEventListener("ended", playRandomSong);
          a.src = songs[Math.floor(Math.random() * songs.length)];
          document.body.appendChild(a);
          a.play();
        }
      function playRandomSong() {
          this.src = songs[Math.floor(Math.random() * songs.length)];
        }
        getPlayer();
});